mapper.select:
        select d.`id`,
        d.`option_id`,
        d.`option_name`,
        d.`appraisal_user_id`,
        d.`examinee_user_id`,
        d.`appraisal_user_name`,
        d.`appraisal_score`,
        d.`appraisal_time`,
        d.`appraisal_mobile`,
        d.`appraisal_id`,
        u.user_post `opinion`,
        r.name `user_name`
        from performance_appraisal_details d
        left join sys_user u on u.user_id = d.appraisal_user_id
        left join sys_user r on r.user_id = d.examinee_user_id

		  		   and id = #{id} 
		  		   and option_id = #{optionId} 
		  		   and option_name = #{optionName} 
		  		   and appraisal_user_id = #{appraisalUserId} 
		  		   and examinee_user_id = #{examineeUserId} 
		  		   and appraisal_user_name = #{appraisalUserName} 
		  		   and appraisal_score = #{appraisalScore} 
                   and appraisal_id = #{appraisalId} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from performance_appraisal_details

		  		   and id = #{id} 
		  		   and option_id = #{optionId} 
		  		   and option_name = #{optionName} 
		  		   and appraisal_user_id = #{appraisalUserId} 
		  		   and examinee_user_id = #{examineeUserId} 
		  		   and appraisal_user_name = #{appraisalUserName} 
		  		   and appraisal_score = #{appraisalScore} 
                   and appraisal_id = #{appraisalId}

select GROUP_CONCAT(a.appraisal_score)appraisal_score,a.name as dept_name from (select d.appraisal_id,sum(d.appraisal_score)as appraisal_score,d.appraisal_user_name,t.name as name from performance_appraisal_details d left join sys_user u on u.user_id = d.appraisal_user_id left join sys_dept t on t.dept_id = u.dept_id
and appraisal_user_id = #{appraisalUserId}
GROUP BY d.appraisal_id,t.name,d.appraisal_user_id)a group by a.name
